/**
 * Official Type definitions for JSS Parser
 * https://jspreadsheet.com/products/parser
 * Definitions: https://github.com/DefinitelyTyped/DefinitelyTyped
 */

interface parser {
    (): any
    [key: string]: any
}

interface options {
    /** Load the XSLX from a remote URL. Bear in mind any potential CORS restrictions using this property. */
    url?: string;
    /** Define the decimal and a thousand separator based on a locale. */
    locale?: string;
    /** load from a local file. This property is used along input type='file' */
    file?: object;
    /** When the file is loaded. */
    onload?: (config: object) => void;
    /** Method to be called when something is wrong. */
    onerror?: (error: object) => void;
}

export declare function parser(options: options): any
